const swiper=new Swiper(".swiper-container",{slidesPerView:1,direction:"horizontal",loop:!0,autoplay:{disableOnInteraction:!1,delay:2e3},pagination:{el:".swiper-pagination",clickable:!0}}),header=document.querySelector(".header"),toTop=document.querySelector(".to-top");toTop.addEventListener("click",e=>{window.scrollTo({top:0,behavior:"smooth"})});const burgerOpen=document.querySelector(".burger-icon"),burgerClose=document.querySelector(".burger__close"),burgerMenu=document.querySelector(".burger__menu"),burgerOverlay=document.querySelector(".burger__overlay");burgerOpen.addEventListener("click",e=>{burgerMenu.classList.add("burger__menu--active"),burgerOverlay.classList.add("burger__overlay--active")}),burgerClose.addEventListener("click",e=>{burgerMenu.classList.remove("burger__menu--active"),burgerOverlay.classList.remove("burger__overlay--active")}),burgerOverlay.addEventListener("click",e=>{burgerMenu.classList.remove("burger__menu--active"),burgerOverlay.classList.remove("burger__overlay--active")});const nav=header.querySelectorAll(".nav__item"),search=document.querySelector(".nav__search"),searchI=document.querySelector(".nav__searchI"),searchInput=document.querySelector(".nav__search-input");searchI.addEventListener("click",e=>{nav.forEach(e=>{e.classList.toggle("nav__item--hidden")}),search.classList.toggle("nav__search--active"),searchInput.focus(),searchInput.value=""});